import React from 'react';
import { SafeAreaView, View, Text, FlatList, TouchableOpacity } from 'react-native';
import { Card } from 'react-native-elements';
import Icon from 'react-native-vector-icons/FontAwesome';
import styles from './cardsScreen.styles';

// Simulando datos de las tarjetas
const cardsData = [
  {
    id: '1',
    discount: '50%',
    title: 'FLAT 50% off upto $50 Surprise Cashback',
    description: 'Only on Healthcare Products On Orders above $100',
    code: 'BCH12'
  },
  // ...otros datos de tarjetas
];

const CardsScreen = () => {
    return (
        <SafeAreaView style={styles.safeArea}>
          <Header />
          <CategoriesMenu />
          <FlatList
            data={cardsData}
            keyExtractor={(item) => item.id}
            renderItem={({ item }) => <OfferCard {...item} />}
            ListHeaderComponent={<CategoriesMenu />} // Pon tu menú de categorías aquí
            // Si necesitas más elementos antes de la lista, añádelos dentro de ListHeaderComponent
            ListFooterComponent={<View style={{ paddingBottom: 20 }} />} // Espacio adicional si es necesario
            // Puedes añadir un pie de página después de la lista usando ListFooterComponent
          />
          <BottomNavBar />
        </SafeAreaView>
      );
};

const Header = () => (
  <View style={styles.headerContainer}>
    <Icon name="arrow-left" size={24} color="#000" onPress={() => {/* lógica de navegación */}} />
    <Text style={styles.headerTitle}>OFFERS</Text>
    <Icon name="search" size={24} color="#000" onPress={() => {/* lógica de búsqueda */}} />
  </View>
);

const CategoriesMenu = () => (
  <View style={styles.categoriesContainer}>
    {/* Botones o tabs para las categorías */}
  </View>
);

const OfferCard = ({ discount, title, description, code }) => (
  <Card>
    <Text style={styles.cardDiscount}>{discount}</Text>
    <Text>{title}</Text>
    <Text style={styles.cardConditions}>{description}</Text>
    <View style={styles.cardFooter}>
      <Text>{code}</Text>
      <TouchableOpacity style={styles.copyButton}>
        <Text>COPY CODE</Text>
      </TouchableOpacity>
    </View>
  </Card>
);

const BottomNavBar = () => (
  <View style={styles.bottomNavContainer}>
    {/* Íconos de navegación */}
  </View>
);

export default CardsScreen;
