import { StyleSheet, Dimensions } from 'react-native';


export default StyleSheet.create({
    headerContainer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: 15,
        backgroundColor: '#fff', // Un color de fondo para el encabezado
        borderBottomColor: '#dedede', // Color de la línea divisoria
        borderBottomWidth: 1, // Grosor de la línea divisoria
      },
      headerTitle: {
        fontSize: 20, // Tamaño de la fuente para el título
        fontWeight: 'bold', // Negrita para el título
        color: '#000', // Color del texto para el título
      },
      categoriesContainer: {
        flexDirection: 'row',
        justifyContent: 'space-evenly',
        backgroundColor: '#f8f8f8', // Un color de fondo suave para las categorías
        paddingVertical: 10, // Espacio vertical
      },
      cardDiscount: {
        fontSize: 24, // Tamaño de la fuente para el descuento
        fontWeight: 'bold', // Negrita para el descuento
        color: '#4CAF50', // Color verde para el texto del descuento
        marginBottom: 5, // Margen en la parte inferior del descuento
      },
      cardConditions: {
        fontSize: 14, // Tamaño de la fuente para las condiciones
        color: '#757575', // Color gris para el texto de las condiciones
        marginBottom: 10, // Margen en la parte inferior de las condiciones
      },
      cardFooter: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        borderTopColor: '#dedede', // Color de la línea divisoria en la parte superior del pie de la tarjeta
        borderTopWidth: 1, // Grosor de la línea divisoria
        paddingTop: 10, // Espacio superior
      },
      copyButton: {
        backgroundColor: '#4CAF50', // Color verde para el botón
        paddingHorizontal: 15, // Espacio horizontal interno del botón
        paddingVertical: 5, // Espacio vertical interno del botón
        borderRadius: 5, // Bordes redondeados del botón
      },
      bottomNavContainer: {
        flexDirection: 'row',
        justifyContent: 'space-around',
        backgroundColor: '#fff', // Un color de fondo para la barra de navegación
        borderTopColor: '#dedede', // Color de la línea divisoria en la parte superior
        borderTopWidth: 1, // Grosor de la línea divisoria
        paddingVertical: 10, // Espacio vertical
      },
      // Estilos adicionales que podrías necesitar para botones de categoría y otros elementos de la UI
      categoryButton: {
        padding: 10, // Espacio interno para los botones de categoría
      },
      categoryButtonText: {
        color: '#000', // Color del texto para los botones de categoría
        fontSize: 16, // Tamaño de la fuente para los botones de categoría
      },
    });
